mapper.select:
SELECT
*
FROM appointment


        tutor_id = #{tutorId}


        AND tutee_id = #{tuteeId}

mapper.update:
UPDATE appointment
SET
    appointment_type_id = #{appointmentListingModelNew.appointmentTypeId},
    tutor_id = #{appointmentListingModelNew.tutorId},
    tutee_id = #{appointmentListingModelNew.tuteeId},
    appointment_date = #{appointmentListingModelNew.appointmentDate},
    start_time_id = #{appointmentListingModelNew.startTimeId},
    end_time_id = #{appointmentListingModelNew.endTimeId},
    location_id = #{appointmentListingModelNew.locationId},
    appointment_status_id = #{appointmentListingModelNew.appointmentStatusId}
WHERE
    tutor_id = #{appointmentListingModelOld.tutorId}
    AND tutee_id = #{appointmentListingModelOld.tuteeId}
    AND appointment_date = #{appointmentListingModelOld.appointmentDate}
    AND start_time_id = #{appointmentListingModelOld.startTimeId}
    AND end_time_id = #{appointmentListingModelOld.endTimeId}
