mapper.select:
SELECT a.appointment_id, a.appointment_size_name, a.cancellation_reason, a.end_date_time AS end_date_time_value, a.is_cancelled, a.is_confirmed, a.location_name, a.start_date_time AS start_date_time_value, a.tutee_email AS tutee_email, tutee.first_name AS tutee_first_name, tutee.last_name AS tutee_last_name, tutee.phone_number AS tutee_phone_number, tutee.picture_url AS tutee_picture_url, a.tutee_request_comment, tutor.first_name AS tutor_first_name, tutor.last_name AS tutor_last_name, tutor.phone_number AS tutor_phone_number, a.tutor_email AS tutor_email, tutor.picture_url AS tutor_picture_url FROM appointment AS a INNER JOIN tutor ON a.tutor_email = tutor.email INNER JOIN tutee ON a.tutee_email = tutee.email
AND a.appointment_id = #{appointmentIdEquals}
AND LOWER(a.appointment_size_name) LIKE LOWER(#{appointmentSizeNameContainsPattern})
AND LOWER(a.cancellation_reason) LIKE LOWER(#{cancellationReasonContainsPattern})
AND a.end_date_time <= STR_TO_DATE(#{endDateTimeLessThanOrEquals}, #{formatPattern})
AND a.is_cancelled = #{isCancelledEquals}
AND a.is_confirmed = #{isConfirmedEquals}
AND a.location_name in
#{item}
AND LOWER(tutee.email) LIKE LOWER(#{tuteeEmailContainsPattern})
AND LOWER(tutor.email) LIKE LOWER(#{tutorEmailContainsPattern})
AND a.start_date_time >= STR_TO_DATE(#{startDateTimeGreaterThanOrEquals}, #{formatPattern})
AND !(is_cancelled = true AND is_confirmed = false)
LIMIT #{limit}
OFFSET #{offset}

SELECT COUNT(*) FROM appointment AS a INNER JOIN tutor ON a.tutor_email = tutor.email INNER JOIN tutee ON a.tutee_email = tutee.email
AND a.appointment_id = #{appointmentIdEquals}
