mapper.select:
SELECT
*
FROM appointment_request


        appointment_id = #{appointmentId}


        AND initiator_type_id = #{initiatorTypeId}

mapper.update:
UPDATE appointment_request
SET
    request_type_id = #{appointmentRequestModelNew.requestTypeId},
    request_status_id = #{appointmentRequestModelNew.requestStatusId}
WHERE
    appointment_id = #{appointmentRequestModelOld.appointmentId}
    AND initiator_type_id = #{appointmentRequestModelOld.initiatorTypeId}
