mapper.select:
SELECT DISTINCT t.active_status_name, t.bio_text, t.email, t.first_name, t.last_name, t.listing_title, t.major_abbreviation, t.pay_rate, t.phone_number, t.picture_url, t.seniority_name, r.average_rating, r.number_of_ratings, rc.number_one_star_ratings, rc.number_two_star_ratings, rc.number_three_star_ratings, rc.number_four_star_ratings, rc.number_five_star_ratings FROM tutor as t LEFT JOIN tutor_average_rating AS r ON t.email = r.email LEFT JOIN tutor_rating_counts AS rc ON t.email = rc.email LEFT JOIN tutor_eligible_course AS e ON t.email = e.tutor_email LEFT JOIN tutor_course_preference AS c ON t.email = c.tutor_email LEFT JOIN tutor_location_preference AS l on t.email = l.tutor_email
AND active_status_name = #{activeStatusNameEquals}
AND average_rating >= #{averageRatingGreaterThanOrEquals}
AND average_rating <= #{averageRatingLessThanOrEquals}
AND LOWER(bio_text) LIKE LOWER(#{bioTextContainsPattern})
AND (LOWER(t.bio_text) LIKE LOWER(#{containsPattern}) OR LOWER(c.course_number) LIKE LOWER(#{containsPattern}) OR LOWER(c.major_abbreviation) LIKE LOWER(#{containsPattern}) OR LOWER(t.email) LIKE LOWER(#{containsPattern}) OR LOWER(t.first_name) LIKE LOWER(#{containsPattern}) OR LOWER(t.last_name) LIKE LOWER(#{containsPattern}) OR LOWER(t.listing_title) LIKE LOWER(#{containsPattern}) OR LOWER(l.location_name) LIKE LOWER(#{containsPattern}) OR LOWER(CONCAT_WS(" ", t.first_name, t.last_name)) LIKE LOWER(#{containsPattern}))
AND LOWER(t.email) LIKE LOWER(#{emailContainsPattern})
AND LOWER(first_name) LIKE LOWER(#{firstNameContainsPattern})
AND LOWER(last_name) LIKE LOWER(#{lastNameContainsPattern})
AND LOWER(listing_title) LIKE LOWER(#{listingTitleContainsPattern})
AND LOWER(t.major_abbreviation) LIKE LOWER(#{majorAbbreviationContainsPattern})
AND number_of_ratings >= #{numberOfRatingsGreaterThanOrEquals}
AND number_of_ratings <= #{numberOfRatingsLessThanOrEquals}
AND pay_rate >= #{payRateGreaterThanOrEquals}
AND pay_rate <= #{payRateLessThanOrEquals}
AND LOWER(phone_number) LIKE LOWER(#{phoneNumberContainsPattern})
AND LOWER(picture_url) LIKE LOWER(#{pictureUrlContainsPattern})
AND seniority_name in
#{item}
AND e.course_grade in
#{item}
AND e.course_number = #{eligibleCourseNumberEquals}
AND e.course_number >= #{eligibleCourseNumberGreaterThanOrEquals}
AND e.course_number <= #{eligibleCourseNumberLessThanOrEquals}
AND LOWER(e.major_abbreviation) LIKE LOWER(#{eligibleCourseMajorAbbreviationContainsPattern})
AND c.course_grade in
#{item}
AND c.course_number = #{coursePreferenceNumberEquals}
AND c.course_number >= #{coursePreferenceNumberGreaterThanOrEquals}
AND c.course_number <= #{coursePreferenceNumberLessThanOrEquals}
AND LOWER(c.major_abbreviation) LIKE LOWER(#{coursePreferenceMajorAbbreviationContainsPattern})
AND l.location_name in
#{item}
ORDER BY average_rating ASC, number_of_ratings ASC
ORDER BY average_rating DESC, number_of_ratings ASC
ORDER BY pay_rate ASC
ORDER BY pay_rate DESC
LIMIT #{limit}
OFFSET #{offset}

SELECT COUNT( DISTINCT t.active_status_name, t.email, t.first_name, t.last_name, t.major_abbreviation, t.pay_rate, t.phone_number, t.seniority_name) FROM tutor as t LEFT JOIN tutor_average_rating AS r ON t.email = r.email LEFT JOIN tutor_rating_counts AS rc ON t.email = rc.email LEFT JOIN tutor_eligible_course AS e ON t.email = e.tutor_email LEFT JOIN tutor_course_preference AS c ON t.email = c.tutor_email LEFT JOIN tutor_location_preference AS l on t.email = l.tutor_email
AND active_status_name = #{activeStatusNameEquals}
